import { useParams } from "react-router-dom";
import { useCallback, useEffect,useState, useReducer } from 'react'
import { CocktailsList } from '../../data/CocktailsList'
import { getCocktails } from '../../services'
import Loader from '../../components/loader'
function DeTailsPage(){

    let { id } = useParams();
    const [cocktail, setCocktail] = useState('cocktails', [])


    const loadCocktails = useCallback(async () => {
        const cocktailslist = await getCocktails()

        const data = cocktailslist.filter((item) =>

        item.idDrink.includes(id)
      ) 
      return data;

      }, [setCocktail])


      console.log(cocktail);
      useEffect(() => {
        const timer = setTimeout(() => {
            loadCocktails()     
        }, 1000);
        return () => { clearTimeout(timer);} 
      }, []);


      const rendercocktails = (cocktail) => {
        return (
            <div>
              <h3>ID: {cocktail}</h3>

            </div>
          );
      }

     return (

         <div >{rendercocktails()}</div>
     )



}

export default DeTailsPage;
