import { Link } from "react-router-dom"
import * as route from '../utils/path'
export const DetailsList = ({ data }) => {

    return (
      <div className="row">
        {data.map((item) => {

          return (
            <div className="container mt-5 w-75 d-flex justify-content-center" key={item.idDrink}>
              <div className="col-md-5">
              <img
                className="card-img-top img-fluid"
                src={item.strDrinkThumb}
              />
              </div>


              <div className="col-md-7 text-left p-5">
              <h3>{item.strDrink} </h3>
              <h6 className="mt-2">{item.strGlass}</h6>
              <p className="mt-2 text-muted">{item.strAlcoholic}</p>
              {/* <Link to={`/details/${item.idDrink}`} ><button className="btn col-md-4 btn-details">Details</button></Link> */}
              </div>

            </div>

          )
        })}
      </div>
    )
  }

  export default DetailsList
